<?php
// Imagen por defecto en base64 (una simple imagen "No disponible")
$imagen_base64 = '';

// Directorio de destino
$upload_dir = "../uploads/reviews/";

// Asegurarse de que el directorio existe
if (!is_dir($upload_dir)) {
    mkdir($upload_dir, 0777, true);
}

// Decodificar la imagen base64
$imagen_decodificada = base64_decode($imagen_base64);

// Guardar la imagen decodificada en un archivo
$archivo_destino = $upload_dir . 'imagen_no_disponible.jpg';
file_put_contents($archivo_destino, $imagen_decodificada);

// Cambiar los permisos del archivo
chmod($archivo_destino, 0644);

echo "Imagen creada exitosamente en $archivo_destino";
?> 